import requests

url = "https://world-countries1.p.rapidapi.com/api/v1/cities/nearby"
querystring = {"latitude": latitude, "per_page": per_page, "page": page, "max_population": max_population, "longitude": longitude, "min_population": min_population, "radius": radius}

headers = {
    "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
    "X-RapidAPI-Host": "world-countries1.p.rapidapi.com"
}

response = requests.get(url, headers=headers, params=querystring)

print(response.json())

